insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('1', '麻黄', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'MH', '克', '辛、微苦，温。归肺、膀胱经。', '发汗解表，宣肺平喘，利水消肿。', '1．风寒感冒。\n2．咳嗽气喘。\n3．风水水肿。', '', '4', '【用法用量】煎服，2～9g。发汗解表宜生用，止咳平喘多炙用。\n【使用注意】本品发汗宣肺力强，凡表虚自汗、阴虚盗汗及肺肾虚喘者均当慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('2', '桂枝', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'GZ', '克', '辛、甘，温。归心、肺、膀胱经。', '发汗解肌，温通经脉，助阳化气。', '1．风寒感冒。\n2．寒凝血滞诸痛证。\n3．痰饮、蓄水证。\n4．心悸。', '', '3', '【用法用量】煎服，3～9g。\n【使用注意】本品辛温助热，易伤阴动血，凡外感热病、阴虚火旺、血热妄行等证，均当忌用。孕妇及月经过多者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('3', '紫苏', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'ZS', '克', '辛，温。归肺、脾经。', '解表散寒，行气宽中。', '1．风寒感冒。\n2．脾胃气滞，胸闷呕吐。', '', '3', '【用法用量】煎服，5～9g，不宜久煎。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('4', '生姜', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'SJ', '克', '辛，温。归肺、脾、胃经。', '解表散寒，温中止呕，温肺止咳。', '1．风寒感冒。\n2．脾胃寒证。\n3．胃寒呕吐。\n4．肺寒咳嗽。', '', '3', '【用法用量】煎服，3～9g，或捣汁服。\n【使用注意】本品助火伤阴，故热盛及阴虚内热者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('5', '香薷', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'XR', '克', '辛，微温。归肺、脾、胃经。', '发汗解表，化湿和中，利水消肿。', '1．风寒感冒。\n2．水肿脚气。', '', '3', '【用法用量】煎服，3～9g。用于发表，量不宜过大，且不宜久煎；用于利水消肿，量宜稍大，且须浓煎。\n【使用注意】本品辛温发汗之力较强，表虚有汗及暑热证当忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('6', '荆芥', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'JJ', '克', '辛，微温。归肺、肝经。', '祛风解表，透疹消疮，止血。', '1．外感表证。\n2．麻疹不透、风疹瘙痒。\n3．疮疡初起兼有表证。\n4．吐衄下血。', '', '3', '【用法用量】煎服，4.5～9g，不宜久煎。发表透疹消疮宜生用；止血宜炒用。荆芥穗更长于祛风。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('7', '黑芥穗', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '荆芥穗炭', 'HJS,JJST', '克', '辛，微温。归肺、肝经。', '祛风解表，止血。', '1．外感表证。\n2．吐衄下血。', '', '3', '');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('8', '防风', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'FF', '克', '辛、甘，微温。归膀胱、肝、脾经。', '祛风解表，胜湿止痛，止痉。', '1．外感表证。\n2．风疹瘙痒。\n3．风湿痹痛。\n4．破伤风证。', '荆芥', '3', '【用法用量】煎服，4.5～9g。\n【使用注意】本品药性偏温，阴血亏虚、热病动风者不宜使用。\n【鉴别用药】荆芥与防风均味辛性微温，温而不燥，长于发表散风，对于外感表证，无论是风寒感冒，恶寒发热、头痛无汗，还是风热感冒，发热、微恶风寒、头痛、咽痛等，两者均可使用。同时，两者也都可用于风疹瘙痒。但荆芥质轻透散，发汗之力较防风为强，风寒感冒、风热感冒均常选用；又能透疹、消疮、止血。防风质松而润，祛风之力较强，为“风药之润剂”、“治风之通用药”，又能胜湿、止痛、止痉，又可用于外感风湿，头痛如裹、身重肢痛等证。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('9', '羌活', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'QH', '克', '辛、苦，温。归膀胱、肾经。', '解表散寒，祛风胜湿，止痛。', '1．风寒感冒。\n2．风寒湿痹。', '', '3', '【用法用量】煎服，3～9g。\n【使用注意】本品辛香温燥之性较烈，故阴血亏虚者慎用。用量过多，易致呕吐，脾胃虚弱者不宜服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('10', '白芷', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'BZ', '克', '辛，温。归肺、胃、大肠经。', '解表散寒，祛风止痛，通鼻窍，燥湿止带，消肿排脓。', '1．风寒感冒。\n2．头痛，牙痛，痹痛等多种疼痛证。\n3．鼻渊。\n4．带下证。\n5．疮痈肿毒。', '', '3', '【用法用量】煎服，3～9g。外用适量。\n【使用注意】本品辛香温燥，阴虚血热者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('11', '细辛', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'XX', '克', '辛,温。有小毒。归肺、肾、心经。', '解表散寒，祛风止痛，通窍，温肺化饮。', '1．风寒感冒。\n2．头痛，牙痛，风湿痹痛。\n3．鼻渊。\n4．肺寒咳喘。', '', '3', '【用法用量】煎服，1～3g；散剂每次服0.5～1g。\n【使用注意】阴虚阳亢头痛，肺燥伤阴干咳者忌用。不宜与藜芦同用。\n【鉴别用药】细辛、麻黄、桂枝皆为辛温解表、发散风寒常用药，均可用治风寒感冒。然麻黄发汗作用较强，主治风寒感冒重证；桂枝发汗解表作用较为和缓，凡风寒感冒，无论表实无汗，表虚有汗均可用之；细辛辛温走窜，达表入里，发汗之力不如麻黄、桂枝，但散寒力胜，适当配伍还常用治寒犯少阴之阳虚外感。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('12', '藁本', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'GB', '克', '辛，温。归膀胱经。', '祛风散寒，除湿止痛。', '1．风寒感冒，巅顶疼痛。\n2．风寒湿痹。\n', '', '3', '【用法用量】煎服，3～9g。 【使用注意】本品辛温香燥，凡阴血亏虚、肝阳上亢、火热内盛之头痛者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('13', '苍耳子', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'CEZ', '克', '辛、苦，温。有毒。归肺经。', '发散风寒，通鼻窍，祛风湿，止痛。', '1．风寒感冒。\n2．鼻渊。\n3．风湿痹痛。', '', '6', '【用法用量】煎服，3～9g。或入丸、散。  【使用注意】血虚头痛不宜服用。过量服用易致中毒。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('14', '辛夷', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'XY', '克', '辛，温。归肺、胃经。', '发散风寒，通鼻窍。', '1．风寒感冒。\n2．鼻渊。', '', '3', '【用法用量】煎服，3～9g；本品有毛，易刺激咽喉，入汤剂宜用纱布包煎。 【使用注意】鼻病因于阴虚火旺者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('15', '葱白', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'CB', '克', '辛，温。归肺、胃经。', '发汗解表，散寒通阳。', '1．风寒感冒。\n2．阴盛格阳。', '', '3', '【用法用量】煎服，3～9g。外用适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('16', '鹅不食草', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'EBSC', '克', '辛，温。归肺、肝经。', '发散风寒，通鼻窍，止咳，解毒。', '1．风寒感冒。\n2．鼻塞不通。\n3．寒痰咳喘。\n4．疮痈肿毒。', '', '3', '【用法用量】煎服，6～10g。外用适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('17', '胡荽', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'HS', '克', '辛，温。归肺、胃经。', '发表透疹，开胃消食。', '1．麻疹不透。\n2．饮食不消，纳食不佳。', '', '3', '【用法用量】煎服，3～6g。外用适量。\n【使用注意】热毒壅盛而疹出不畅者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('18', '柽柳', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛温解表'), '', 'CL', '克', '辛，平。归肺、胃、心经。', '发表透疹，祛风除湿。', '1．麻疹不透，风疹瘙痒。\n2．风湿痹痛。', '', '3', '【用法用量】煎服，3～10g。外用适量。\n【使用注意】麻疹已透者不宜使用。用量过大易致心烦、呕吐。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('19', '薄荷', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛凉解表'), '', 'BH', '克', '辛，凉。归肺、肝经。', '疏散风热，清利头目，利咽透疹，疏肝行气。', '1．风热感冒，温病初起。\n2．头痛眩晕，目赤多泪，咽喉肿痛。\n3．麻疹不透，风疹瘙痒。\n4．肝郁气滞，胸闷胁痛。', '', '3', '【用法用量】煎服，3～6g；宜后下。\n【使用注意】本品芳香辛散，发汗耗气，故体虚多汗者不宜使用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('20', '牛蒡子', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛凉解表'), '', 'NBZ,LBZ', '克', '辛、苦，寒。归肺、胃经。', '疏散风热，宣肺祛痰，利咽透疹，解毒消肿。', '1．风热感冒，温病初起。\n2．麻疹不透，风疹瘙痒。\n3．痈肿疮毒，丹毒，痄腮喉痹。', '', '3', '【用法用量】煎服，6～12g。炒用可使其苦寒及滑肠之性略减。\n【使用注意】本品性寒，滑肠通便，气虚便溏者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('21', '蝉蜕', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛凉解表'), '', 'CT', '克', '甘，寒。归肺、肝经。', '疏散风热，利咽开音，透疹，明目退翳，息风止痉。', '1．风热感冒，温病初起，咽痛音哑。\n2．麻疹不透，风疹瘙痒。\n3．目赤翳障。\n4．急慢惊风，破伤风证。', '', '3', '【用法用量】煎服，3～10g，或单味研末冲服。一般病证用量宜小；止痉则需大量。\n【使用注意】《名医别录》有“主妇人生子不下”的记载，故孕妇当慎用。\n【鉴别用药】薄荷、牛蒡子与蝉蜕三药皆能疏散风热、透疹、利咽，均可用于外感风热或温病初起，发热、微恶风寒、头痛；麻疹初起，透发不畅；风疹瘙痒；风热上攻，咽喉肿痛等证。但薄荷辛凉芳香，清轻凉散，发汗之力较强，故外感风热、发热无汗者薄荷首选。且薄荷又能清利头目、疏肝行气。牛蒡子辛散苦泄，性寒滑利，兼能宣肺祛痰，故外感风热、发热、咳嗽、咯痰不畅者，牛蒡子尤为适宜。同时，牛蒡子外散风热，内解热毒，有清热解毒散肿之功。蝉蜕甘寒质轻，既能疏散肺经风热而利咽、透疹、止痒，又长于疏散肝经风热而明目退翳，凉肝息风止痉。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('22', '桑叶', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛凉解表'), '', 'SY', '克', '甘、苦，寒。归肺、肝经。', '疏散风热，清肺润燥，平抑肝阳，清肝明目。', '1．风热感冒，温病初起。\n2．肺热咳嗽、燥热咳嗽。\n3．肝阳上亢。\n4．目赤昏花。', '', '3', '【用法用量】煎服，5～9g；或入丸、散。外用煎水洗眼。桑叶蜜制能增强润肺止咳的作用，故肺燥咳嗽多用蜜制桑叶。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('23', '菊花', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛凉解表'), '', 'JH', '克', '辛、甘、苦，微寒。归肺、肝经。', '疏散风热，平抑肝阳，清肝明目，清热解毒。', '1．风热感冒，温病初起。\n2．肝阳上亢。\n3．目赤昏花。\n4．疮痈肿毒。', '桑叶', '3', '【用法用量】煎服，5～9g。疏散风热宜用黄菊花，平肝、清肝明目宜用白菊花。\n【鉴别用药】桑叶与菊花皆能疏散风热，平抑肝阳，清肝明目，同可用治风热感冒或温病初起，发热、微恶风寒、头痛；肝阳上亢，头痛眩晕；风热上攻或肝火上炎所致的目赤肿痛，以及肝肾精血不足，目暗昏花等证。但桑叶疏散风热之力较强，又能清肺润燥，凉血止血。菊花平肝、清肝明目之力较强，又能清热解毒。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('24', '蔓荆子', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛凉解表'), '', 'MJZ', '克', '辛、苦，微寒。归膀胱、肝、胃经。', '疏散风热，清利头目。', '1．风热感冒，头昏头痛。\n2．目赤肿痛。', '', '3', '【用法用量】煎服，5～9g。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('25', '柴胡', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛凉解表'), '', 'CH', '克', '苦、辛，微寒。归肝、胆经。', '解表退热，疏肝解郁，升举阳气。', '1．表证发热及少阳证。\n2．肝郁气滞。\n3．气虚下陷，脏器脱垂。', '', '3', '【用法用量】煎服，3～9g。解表退热宜生用，且用量宜稍重；疏肝解郁宜醋炙，升阳可生用或酒炙，其用量均宜稍轻。\n【使用注意】柴胡其性升散，古人有“柴胡劫肝阴”之说，阴虚阳亢，肝风内动，阴虚火旺及气机上逆者忌用或慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('26', '升麻', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛凉解表'), '', 'SM', '克', '辛、微甘，微寒。归肺、脾、胃、大肠经。', '解表透疹，清热解毒，升举阳气。', '1．外感表证。\n2．麻疹不透。\n3．齿痛口疮，咽喉肿痛，温毒发斑。\n4．气虚下陷，脏器脱垂，崩漏下血。', '', '3', '【用法用量】煎服，3～9g。发表透疹、清热解毒宣生用，升阳举陷宜炙用。\n【使用注意】麻疹已透，阴虚火旺，以及阴虚阳亢者，均当忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('27', '葛根', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛凉解表'), '干葛', 'GG', '克', '甘、辛，凉。归脾、胃经。', '解肌退热，透疹，生津止渴，升阳止泻。', '1．表证发热，项背强痛。\n2．麻疹不透。\n3．热病口渴，消渴证。\n4．热泄热痢，脾虚泄泻。', '', '3', '【用法用量】煎服，9～15g。解肌退热、透疹、生津宜生用，升阳止泻宜煨用。\n【鉴别用药】柴胡、升麻、葛根三者皆能发表、升阳，均可用治风热感冒、发热、头痛，以及清阳不升等证。其中：柴胡、升麻两者均能升阳举陷，用治气虚下陷，食少便溏、久泻脱肛、胃下垂、肾下垂、子宫脱垂等脏器脱垂；升麻、葛根两者又能透疹，常用治麻疹初起、透发不畅。但柴胡主升肝胆之气，长于疏散少阳半表半里之邪、退热，疏肝解郁，为治疗少阳证的要药。又常用于伤寒邪在少阳，寒热往来、胸胁苦满、口苦咽干、目眩；感冒发热；肝郁气滞，胸胁胀痛、月经不调、痛经等证。升麻主升脾胃清阳之气，其升提（升阳举陷）之力较柴胡为强，并善于清热解毒，又常用于多种热毒病证。葛根主升脾胃清阳之气而达到生津止渴、止泻之功，常用于热病烦渴，阴虚消渴；热泄热痢，脾虚泄泻。同时，葛根解肌退热，对于外感表证，发热恶寒、头痛无汗、项背强痛，无论风寒表证、风热表证，均可使用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('28', '淡豆豉', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛凉解表'), '豆豉,香豉', 'DDC,DDQ,DQ,XQ', '克', '苦、辛，凉。归肺、胃经。', '解表，除烦，宣发郁热。', '1．外感表证。\n2．热病烦闷。', '', '3', '【用法用量】煎服，6～12g。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('29', '浮萍', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛凉解表'), '', 'FP', '克', '辛，寒。归肺、膀胱经。', '发汗解表，透疹止痒，利尿消肿。', '1．风热感冒。\n2．麻疹不透。\n3．风疹瘙痒。\n4．水肿尿少。', '', '3', '【用法用量】煎服3～9g。外用适量，煎汤浸洗。 【使用注意】表虚自汗者不宜使用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('30', '木贼', (select PK_ID from SYNDROME_SUBJECT where NAME = '__辛凉解表'), '', 'MZ', '克', '甘、苦，平。归肺、肝经。', '疏散风热，明目退翳。', '1．风热目赤，迎风流泪，目生翳障。\n2．出血证。', '', '3', '【用法用量】煎服，3～9g。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('31', '石膏', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'SG', '克', '甘、辛，大寒。归肺、胃经。', '生用：清热泻火，除烦止渴；\n煅用：敛疮生肌，收湿，止血。', '1．温热病气分实热证。\n2．肺热喘咳证。\n3．胃火牙痛、头痛、消渴证。\n4．溃疡不敛、湿疹瘙痒、水火烫伤、外伤出血。', '', '3', '【用法用量】生石膏煎服，15-60g, 宜先煎。 煅石膏适量外用，研末撒敷患处。\n【使用注意】脾胃虚寒及阴虚内热者忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('32', '寒水石', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'HSS', '克', '辛、咸，寒。归心、胃、肾经。', '清热泻火。', '1．热病烦渴、癫狂。\n2．口疮、热毒疮肿、丹毒烫伤。', '', '3', '【用法用量】煎服，10—15g。外用适量。\n【使用注意】脾胃虚寒者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('33', '知母', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'ZM', '克', '苦、甘，寒。归肺、胃、肾经。', '清热泻火，生津润燥。', '1．热病烦渴。\n2．肺热燥咳。\n3．骨蒸潮热。\n4．内热消渴。\n5．肠燥便秘。', '石膏', '3', '【用法用量】煎服，6—12g。\n【使用注意】本品性寒质润，有滑肠作用，故脾虚便溏者不宜用。\n【鉴别用药】石膏、知母均能清热泻火，可用治温热病气分热盛及肺热咳嗽等证。但石膏泻火之中长于清解，重在清泻肺胃实火，肺热喘咳、胃火头痛牙痛多用石膏；知母泻火之中长于清润，肺热燥咳、内热骨蒸、消渴多选知母。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('34', '芦根', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'LG', '克', '甘，寒。归肺、胃经。', '清热泻火，生津止渴，除烦，止呕，利尿。', '1．热病烦渴。\n2．胃热呕哕。\n3．肺热咳嗽，肺痈吐脓。\n4．热淋涩痛。', '', '3', '【用法用量】煎服，干品15—30g；鲜品加倍，或捣汁用。\n【使用注意】脾胃虚寒者忌服。\n【鉴别用药】芦根为芦苇的根茎，苇茎为芦苇的嫩茎。二者出自同一种植物，功效相近。但芦根长于生津止渴，苇茎长于清透肺热，略有侧重。药市中多无苇茎供应，可以芦根代之。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('35', '天花粉', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'THF', '克', '甘、微苦，微寒。归肺、胃经。', '清热泻火，生津止渴，消肿排脓。', '1．热病烦渴。\n2．肺热燥咳。\n3．内热消渴。\n4．疮疡肿毒。', '', '3', '【用法用量】煎服，10-15g。\n【使用注意】不宜于乌头类药材同用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('36', '栝蒌实', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热化痰'), '', 'GLS', '克', '味苦，气寒，降也，阴也，无毒。\n入肺、胃二经。', '下气涤秽，尤消郁开胃.', '治伤寒结胸，祛痰，又解渴生滓，下乳。', '', '6', '【使用注意】切戒轻用，必积秽滞气结在胸上，而不肯不者，始可用之以荡涤，否则，万万不可孟浪。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('37', '竹叶', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'ZY', '克', '甘、辛、淡，寒。归心、胃、小肠经。', '清热泻火，除烦，生津，利尿。', '1．热病烦渴。\n2．口疮尿赤。', '', '3', '【用法用量】煎服，6—15g；鲜品15—30g。\n【使用注意】阴虚火旺，骨蒸潮热者忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('38', '淡竹叶', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'DZY', '克', '甘、淡，寒。归心、胃、小肠经。', '清热泻火，除烦，利尿。', '1．热病烦渴。\n 2．口疮尿赤、热淋涩痛。', '', '3', '【用法用量】煎服，6—9g。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('39', '鸭跖草', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'YZC', '克', '甘、淡，寒。归肺、胃、小肠经。', '清热泻火，解毒，利水消肿。', '1．风热感冒、高热烦渴。\n2．咽喉肿痛、痈疮疔毒。\n3．水肿尿少、热淋涩痛。', '', '3', '【用法用量】煎服，15—30g。鲜品60-90g。\n【使用注意】脾胃虚弱者，用量宜少。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('40', '栀子', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'ZZ', '克', '苦，寒。归心、肺、三焦经。', '泻火除烦，清热利湿，凉血解毒。焦栀子：凉血止血。', '1．热病心烦。\n2．湿热黄疸。\n3．血淋涩痛。\n4．血热吐衄。\n5．目赤肿痛。\n6．火毒疮疡。', '', '3', '【用法用量】煎服，5—10克。外用生品适量，研末调敷。\n【使用注意】本品苦寒伤胃，脾虚便溏者不宜用。\n【鉴别用药】栀子入药，除果实全体入药外，还有果皮、种子分开用者。栀子皮（果皮）偏于达表而去肌肤之热；栀子仁（种子）偏于走里而清内热。生栀子走气分而泻火，焦栀子入血分而止血。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('41', '栀子皮', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '栀皮', 'ZZP,ZP', '克', '苦，寒。归心、肺、三焦经。', '泻火除烦.', '去肌肤之热.', '', '3', '【使用注意】本品苦寒伤胃，脾虚便溏者不宜用。\n【鉴别用药】栀子入药，除果实全体入药外，还有果皮、种子分开用者。栀子皮（果皮）偏于达表而去肌肤之热；栀子仁（种子）偏于走里而清内热。生栀子走气分而泻火，焦栀子入血分而止血。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('42', '栀子仁', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '栀仁', 'ZZR,ZR', '克', '苦，寒。归心、肺、三焦经。', '清热利湿.', '清内热。', '', '3', '【使用注意】本品苦寒伤胃，脾虚便溏者不宜用。\n【鉴别用药】栀子入药，除果实全体入药外，还有果皮、种子分开用者。栀子皮（果皮）偏于达表而去肌肤之热；栀子仁（种子）偏于走里而清内热。生栀子走气分而泻火，焦栀子入血分而止血。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('43', '焦栀', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清营凉血'), '', 'JZ', '克', '苦，寒。归心、肺、三焦经。', '凉血止血。', '入血分而止血。', '', '3', '【使用注意】本品苦寒伤胃，脾虚便溏者不宜用。\n【鉴别用药】栀子入药，除果实全体入药外，还有果皮、种子分开用者。栀子皮（果皮）偏于达表而去肌肤之热；栀子仁（种子）偏于走里而清内热。生栀子走气分而泻火，焦栀子入血分而止血。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('44', '梨皮', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'LP,NP', '克', '甘；涩；凉；无毒.\n归肺；心；肾；大肠经.', '\n清心润肺；降火生津，解疮毒。', '主暑热烦渴；肺燥咳嗽；吐血；痢疾；；疥癣；发背；疔疮.', '', '2', '\n【用法用量】内服：煎汤，9-15g，鲜品30-60g。外用：适量，捣汁涂。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('45', '夏枯草', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'XKC', '克', '辛、苦，寒。归肝、胆经。', '清热泻火，明目，散结消肿。', '1．目赤肿痛、头痛眩晕、目珠夜痛。\n2．瘰疬、瘿瘤。\n3．乳痈肿痛。', '', '3', '【用法用量】煎服，9～15g。或熬膏服。\n【使用注意】脾胃寒弱者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('46', '决明子', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'JMZ', '克', '甘、苦、咸，微寒。归肝、大肠经。', '清热明目，润肠通便。', '1．目赤肿痛、羞明多泪、目暗不明。\n2．头痛、眩晕。\n3．肠燥便秘。', '', '3', '【用法用量】煎服，10～15克；用于润肠通便，不宜久煎。\n【使用注意】气虚便溏者不宜用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('47', '谷精草', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'GJC', '克', '辛、甘，平。归肝、肺经。', '疏散风热，明目，退翳。', '1．风热目赤肿痛、羞明、眼生翳膜。\n2．风热头痛：取其疏散风热而治风热头痛，常配薄荷、菊花、牛蒡子等药用。', '', '3', '【用法用量】煎服，5～10克。\n【使用注意】阴虚血亏之眼疾者不宜用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('48', '密蒙花', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'MMH', '克', '甘，微寒。归肝、胆经。', '清热泻火，养肝明目，退翳。', '1．目赤肿痛、羞明多泪、眼生翳膜。\n2．肝虚目暗、视物昏花。', '', '3', '【用法用量】煎服，9～15g。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('49', '青葙子', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清气分热'), '', 'QXZ', '克', '苦，微寒。归肝、脾经。', '清热泻火，明目退翳。', '1．肝热目赤、眼生翳膜、视物昏花。\n2．肝火眩晕。', '', '3', '【用法用量】煎服，10—15g。\n【使用注意】本品有扩散瞳孔作用，青光眼患者禁用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('50', '黄芩', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'HQ', '克', '苦，寒。归肺、胆、脾、胃、大肠、小肠经。', '清热燥湿，泻火解毒，止血，安胎。', '1．湿温、暑湿、胸闷呕恶，湿热痞满、黄疸泻痢。\n2．肺热咳嗽、高热烦渴。\n3．血热吐衄。\n4．痈肿疮毒。\n5．胎动不安。', '', '3', '【用法用量】煎服，3～10g。清热多生用，安胎多炒用，清上焦热可酒炙用，止血可炒炭用。\n【使用注意】本品苦寒伤胃，脾胃虚寒者不宜使用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('51', '黄连', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'HL', '克', '苦，寒。归心，脾、胃、胆、大肠经。', '清热燥湿，泻火解毒。', '1．湿热痞满、呕吐吞酸。\n2．湿热泻痢。\n3．高热神昏，心烦不寐，血热吐衄。\n4．痈肿疖疮，目赤牙痛。\n5．消渴。\n6．外治湿疹、湿疮、耳道流脓。', '', '3', '【用法用量】煎服，2—5g。外用适量。\n【使用注意】本品大苦大寒，过服久服易伤脾胃，脾胃虚寒者忌用；苦燥易伤阴津，阴虚津伤者慎用。\n【鉴别用药】本品入药，除生用外，还有酒炙、姜汁炙、吴茱萸水炙等特殊炮制品，其功用各有区别。酒黄连善清上焦火热，多用于目赤肿痛、口疮；姜黄连善清胃和胃止呕，多用治寒热互结，湿热中阻，痞满呕吐；萸黄连善舒肝和胃止呕，多用治肝胃不和之呕吐吞酸。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('52', '黄柏', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'HB', '克', '苦，寒。归肾、膀胱、大肠经。', '清热燥湿，泻火除蒸，解毒疗疮。', '1．湿热带下、热淋。\n2．湿热泻痢、黄疸。\n3．湿热脚气、痿证。\n4．骨蒸劳热，盗汗，遗精。\n5．疮疡肿毒、湿疹瘙痒。', '', '3', '【用法用量】煎服，3-12g。外用适量。\n【鉴别用药】黄芩、黄连、黄柏三药性味皆苦寒，而黄连为苦寒之最。三药均以清热燥湿、泻火解毒为主要功效，用治湿热内盛或热毒炽盛之证，常相须为用。但黄芩偏泻上焦肺火，肺热咳嗽者多用；黄连偏泻中焦胃火，并长于泻心火，中焦湿热、痞满呕逆及心火亢旺、高热心烦者多用；黄柏偏泻下焦相火、除骨蒸，湿热下注诸证及骨蒸劳热者多用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('53', '龙胆', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'LD', '克', '苦，寒。归肝、胆经。', '清热燥湿，泻肝胆火。', '1．湿热黄疸、阴肿阴痒、带下、湿疹瘙痒。\n2．肝火头痛、目赤耳聋、胁痛口苦。\n3．惊风抽搐。', '', '3', '【用法用量】煎服,3—6g。\n【使用注意】脾胃寒者不宜用，阴虚津伤者慎用．');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('54', '秦皮', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'QP', '克', '苦、涩，寒。归肝、胆、大肠经。', '清热燥湿，收涩止痢，止带，明目。', '1．湿热泻痢、带下。\n2．肝热目赤肿痛、目生翳膜。', '', '3', '【用法用量】煎服，6—12g。外用适量，煎洗患处。\n【使用注意】脾胃虚寒者忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('55', '苦参', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'KC', '克', '苦，寒。归心、肝、胃、大肠、膀胱经。', '清热燥湿，杀虫，利尿。', '1．湿热泻痢、便血、黄疸。\n2．湿热带下、阴肿阴痒、湿疹湿疮、皮肤瘙痒、疥癣。\n3．湿热小便不利。', '', '3', '【用法用量】煎服,5—10g。外用适量。\n【使用注意】脾胃虚寒者忌用，反藜芦。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('56', '白鲜皮', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'BXP', '克', '苦，寒。归脾、胃、膀胱经。', '清热燥湿，祛风解毒。', '1．湿热疮毒、湿疹，疥癣。\n2．湿热黄疸，风湿热痹。', '', '3', '【用法用量】煎服，5-10g。外用适量。\n【使用注意】脾胃虚寒者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('57', '苦豆子', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'KDZ', '克', '苦、寒。有毒。归胃、大肠经。', '清热燥湿，止痛，杀虫。', '1．湿热泻痢。\n2．胃脘痛，吞酸。\n3．湿疹、顽癣。\n4．白带过多。\n5．疮疖、溃疡。', '', '6', '【用法用量】全草煎汤服，1.5～3g。种子炒用，研末服，每次5粒。\n【使用注意】本品有毒，内服用量不宜过大。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('58', '三棵针', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'SKZ', '克', '苦、寒。有毒。归肝、胃、大肠经。', '清热燥湿，泻火解毒。', '1．湿热泻痢、黄疸、湿疹。\n2．痈肿疮毒，咽喉肿痛，目赤肿痛。', '', '3', '【用法用量】煎服，10～15g。外用适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('59', '马尾连', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'MWL', '克', '苦，寒。归心、肺、肝、胆、大肠经。', '清热燥湿，泻火解毒。', '1．湿热泻痢、黄疸。\n2．热病烦躁。\n3．肺热咳嗽。\n4．痈疮肿毒，目赤肿痛。', '', '3', '【用法用量】煎服，6-12g；全草15-30g。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('60', '金银花', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'JYH', '克', '甘，寒。归肺、心、胃经。', '清热解毒，疏散风热。', '1．痈肿疔疮。\n2．外感风热，温病初起。\n3．热毒血痢。\n', '', '3', '【用法用量】煎服，6～15g。疏散风热、清泄里热以生品为佳；炒炭宜用于热毒血痢；露剂多用于暑热烦渴。\n【使用注意】脾胃虚寒及气虚疮疡脓清者忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('61', '连翘', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'LQ', '克', '苦，微寒，归肺、心、小肠经。', '清热解毒，消肿散结，疏散风热。', '1．痈肿疮毒，瘰疬痰核。\n2．风热外感，温病初起。\n3．热淋涩痛。', '金银花', '3', '【用法用量】煎服，6～15g。\n       【使用注意】脾胃虚寒及气虚脓清者不宜用。\n【鉴别用药】连翘临床有青翘、老翘及连翘心之分。青翘，其清热解毒之力较强；老翘，长于透热达表，而疏散风热；连翘心，长于清心泻火，常用治邪入心包的高热烦躁、神昏谵语等症。\n连翘与金银花，均有清热解毒作用，既能透热达表，又能清里热而解毒。对外感风热、温病初起、热毒疮疡等证常相须为用。然区别点是：连翘清心解毒之力强，并善于消痈散结，为疮家圣药，亦治瘰疬痰核；而金银花疏散表热之效优，且炒炭后善于凉血止痢，用治热毒血痢。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('62', '穿心莲', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'CXL', '克', '苦，寒。归心、肺、大肠、膀胱经。', '清热解毒，凉血，消肿，燥湿。', '1．外感风热，温病初起。\n2．肺热咳喘，肺痈吐脓，咽喉肿痛。\n3．湿热泻痢，热淋涩痛，湿疹瘙痒。', '', '4', '【用法用量】煎服，6～9g。煎剂易致呕吐，故多作丸、散、片剂。外用适量。\n【使用注意】不宜多服久服；脾胃虚寒者不宜用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('63', '大青叶', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'DQY', '克', '苦、寒。归心、胃经。', '清热解毒，凉血消斑。', '1．热入营血，温毒发斑。\n2．喉痹口疮，痄腮丹毒。', '', '3', '【用法用量】煎服，9～15g，鲜品30～60g。外用适量。\n【使用注意】脾胃虚寒者忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('64', '板蓝根', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'BLG', '克', '苦，寒。归心、胃经。', '清热解毒，凉血，利咽。', '1．外感发热，温病初起，咽喉肿痛。\n2．温毒发斑，痄腮，丹毒，痈肿疮毒。\n', '大青叶', '3', '【用法用量】煎服， 9～15g。\n【使用注意】体虚而无实火热毒者忌服，脾胃虚寒者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('65', '青黛', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'QD', '克', '咸，寒。归肝、肺经。', '清热解毒，凉血消斑，清肝泻火，定惊。', '1．温毒发斑，血热吐衄。\n2．咽痛口疮，火毒疮疡。\n3．咳嗽胸痛，痰中带血。\n4．暑热惊痫，惊风抽搐。', '大青叶', '3', '【用法用量】内服1．5～3g，本品难溶于水，一般作散剂冲服，或入丸剂服用。外用适量。\n【使用注意】胃寒者慎用。\n【鉴别用药】大青叶为菘蓝叶；板蓝根为菘蓝或马蓝的根；青黛为马蓝、蓼蓝或菘蓝的茎叶经加工制得的粉未。三者大体同出一源，功效亦相近，皆有清热解毒、凉血消斑之作用。相比较而言，大青叶凉血消斑力强；板蓝根解毒利咽效著；青黛清肝定惊功胜。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('66', '贯众', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'GZ', '克', '苦，微寒。有小毒。归肝、脾经。', '清热解毒，凉血止血，杀虫。', '1．风热感冒，温毒发斑。\n2．血热出血。\n3．虫疾。', '', '4', '【用法用量】煎服，4.5～9g。杀虫及清热解毒宜生用；止血宜炒炭用。外用适量。\n【使用注意】本品有小毒，用量不宜过大。服用本品时忌油腻。脾胃虚寒者及孕妇慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('67', '蒲公英', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'PGY', '克', '苦、甘，寒。归肝、胃经。', '清热解毒，消肿散结，利湿通淋。', '1．痈肿疔毒，乳痈内痈。\n2．热淋涩痛，湿热黄疸。', '', '2', '【用法用量】煎服，9~15g。外用鲜品适量捣敷或煎汤熏洗患处。\n【使用注意】用量过大，可致缓泻。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('68', '紫花地丁', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'ZHDD', '克', '苦、辛，寒。归心、肝经。', '清热解毒，凉血消肿。', '1．疔疮肿毒，乳痈肠痈。\n2．毒蛇咬伤。', '', '3', '【用法用量】煎服，15～30g。外用鲜品适量，捣烂敷患处。\n【使用注意】体质虚寒者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('69', '野菊花', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'YJH', '克', '苦、辛，微寒。归肝、心经。', '清热解毒。', '1．痈疽疔疖，咽喉肿痛。\n2．目赤肿痛，头痛眩晕。', '', '3', '【用法用量】煎服，10～15g。外用适量。\n【鉴别用药】野菊花与菊花为同科植物，均有清热解毒之功，但野菊花苦寒之性尤胜，长于解毒消痈，疮痈疔毒肿痛多用之；而菊花辛散之力较强，长于清热疏风，上焦头目风热多用之。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('70', '重楼', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'ZL', '克', '苦，微寒。有小毒。归肝经。', '清热解毒，消肿止痛，凉肝定惊。', '1．痈肿疔疮，咽喉肿痛，毒蛇咬伤。\n2．惊风抽搐。\n3．跌打损伤。', '', '3', '【用法用量】煎服，3～9g。外用适量，捣敷或研末调涂患处。\n【使用注意】体虚、无实火热毒者、孕妇及患阴证疮疡者均忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('71', '拳参', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'QC', '克', '苦、涩，微寒。归肺、肝、大肠经。', '清热解毒，凉血止血，镇肝息风。', '1．痈肿瘰疬，毒蛇咬伤。\n2．热病神昏，惊痫抽搐。\n3．热泻热痢。\n4．血热出血。', '', '3', '【用法用量】煎服，4.5～9g。外用适量。\n【使用注意】无实火热毒者不宜使用。阴证疮疡患者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('72', '漏芦', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'LL', '克', '苦，寒。归胃经。', '清热解毒，消痈散结，通经下乳，舒筋通脉。', '1．乳痈肿痛，瘰疬疮毒。\n2．乳汁不下。\n3．湿痹拘挛。', '', '3', '【用法用量】煎服，5～9g。外用，研末调敷或煎水洗。\n【使用注意】气虚、疮疡平塌者及孕妇忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('73', '土茯苓', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'TFL', '克', '甘、淡，平。归肝、胃经。', '解毒，除湿，通利关节。', '1．杨梅毒疮，肢体拘挛。\n2．淋浊带下，湿疹瘙痒。\n3．痈肿疮毒。', '', '3', '【用法用量】煎服，15～60g。外用适量。\n【使用注意】肝肾阴虚者慎服。服药时忌茶。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('74', '鱼腥草', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'YXC', '克', '辛，微寒。归肺经。', '清热解毒，消痈排脓，利尿通淋。', '1．肺痈吐脓，肺热咳嗽。\n2．热毒疮毒。\n3．湿热淋证。', '', '3', '【用法用量】煎服，15～25g。鲜品用量加倍，水煎或捣汁服。外用适量，捣敷或煎汤熏洗患处。\n【使用注意】本品含挥发油，不宜久煎。虚寒证及阴性疮疡忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('75', '金荞麦', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'JQM', '克', '微辛、涩，凉。归肺经。', '清热解毒，排脓祛瘀。', '1．肺痈，肺热咳嗽。\n2．瘰疬疮疖，咽喉肿痛。', '', '3', '【用法用量】煎服，15～45g。亦可用水或黄酒隔水密闭炖服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('76', '大血藤', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'DXT', '克', '苦，平。归大肠、肝经。', '清热解毒，活血，祛风，止痛。', '1．肠痈腹痛，热毒疮疡。\n2．跌打损伤，经闭痛经。\n3．风湿痹痛。', '', '3', '【用法用量】煎服，9～15g。外用适量。\n【使用注意】孕妇慎服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('77', '败酱草', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'BJC', '克', '辛、苦，微寒。归胃、大肠、肝经。', '清热解毒，消痈排脓，祛瘀止痛。', '1．肠痈肺痈，痈肿疮毒。\n2．产后瘀阻腹痛。', '', '3', '【用法用量】煎服，6～15g。外用适量。\n【使用注意】脾胃虚弱，食少泄泻者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('78', '射干', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'SG', '克', '苦，寒。归肺经。', '清热解毒，消痰，利咽。', '1．咽喉肿痛。\n2．痰盛咳喘。', '', '3', '【用法用量】煎服，3～9g。\n【使用注意】本品苦寒，脾虚便溏者不宜使用。孕妇忌用或慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('79', '山豆根', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'SDG', '克', '苦，寒。有毒。归肺、胃经。', '清热解毒，利咽消肿。', '1．咽喉肿痛。\n2．牙龈肿痛。', '', '3', '【用法用量】煎服，3～6g。外用适量。\n【使用注意】本品有毒，过量服用易引起呕吐、腹泻、胸闷、心悸等副作用，故用量不宜过大。脾胃虚寒者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('80', '马勃', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'MB', '克', '辛，平。归肺经。', '清热解毒，利咽，止血。', '1．咽喉肿痛，咳嗽失音。\n2．吐血衄血，外伤出血。', '', '3', '【用法用量】煎服，1.5～6g，布包煎；或入丸、散。外用适量，研末撒，或调敷患处，或作吹药。\n【使用注意】风寒伏肺咳嗽失音者禁服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('81', '青果', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'QG', '克', '甘、酸，平。归肺、胃经。', '清热解毒，利咽，生津。', '1．咽喉肿痛，咳嗽烦渴。\n2．鱼蟹中毒。', '', '3', '【用法用量】煎服，4.5～9g；鲜品尤佳，可用至30~50g。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('82', '锦灯笼', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'JDL', '克', '苦、寒。归肺经。', '清热解毒，利咽化痰，利尿通淋。', '1．咽痛音哑，痰热咳嗽。\n2．小便不利，热淋涩痛。', '青果', '3', '【用法用量】煎服，5～9g。外用适量，捣敷患处。\n【使用注意】脾虚泄泻者及孕妇忌用。\n【鉴别用药】锦灯笼与青果，皆能清热解毒利咽，而治咽喉肿痛。但前者偏于化痰利咽，宜治痰热咳嗽，咽痛音哑；而后者偏于生津利咽，宜治咽干口燥，烦渴音哑。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('83', '金果榄', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'JGL', '克', '苦，寒。归肺、大肠经。', '清热解毒，利咽，止痛。', '1．咽喉肿痛。\n2．痈肿疔毒。', '', '3', '【用法用量】煎服，3～9g。外用适量。\n【使用注意】脾胃虚弱者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('84', '木蝴蝶', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'MHD', '克', '苦、甘，凉。归肺、肝、胃经。', '清肺利咽，疏肝和胃。', '1．喉痹音哑，肺热咳嗽。\n2．肝胃气痛。', '', '3', '【用法用量】煎服，1.5～3g。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('85', '白头翁', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'BTW', '克', '苦，寒。归胃、大肠经。', '清热解毒，凉血止痢。', '1．热毒血痢。\n2．疮痈肿毒。', '', '3', '【用法用量】煎服，9～15g，鲜品15~30g。外用适量。\n【使用注意】虚寒泄痢忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('86', '马齿苋', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'MCX', '克', '酸，寒。归肝、大肠经。', '清热解毒，凉血止血，止痢。', '1．热毒血痢。\n2．热毒疮疡。\n3．崩漏，便血。', '', '3', '【用法用量】煎服，9～15g，鲜品30~60g。外用适量，捣敷患处。\n【使用注意】脾胃虚寒，肠滑作泄者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('87', '鸦胆子', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'YDZ', '克', '苦，寒。有小毒。归大肠、肝经。', '清热解毒，止痢，截疟，腐蚀赘疣。', '1．热毒血痢，冷积久痢。\n2．各型疟疾。\n3．鸡眼赘疣。', '', '8', '【用法用量】内服，0.5~2g，以干龙眼肉包裹或装入胶囊包裹吞服，亦可压去油制成丸剂、片剂服，不宜入煎剂。外用适量。\n【使用注意】本品有毒，对胃肠道及肝肾均有损害，内服需严格控制剂量，不宜多用久服。外用注意用胶布保护好周围正常皮肤，以防止对正常皮肤的刺激。孕妇及小儿慎用。胃肠出血及肝肾病患者，应忌用或慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('88', '地锦草', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'DJC', '克', '辛，平。归肝、大肠经。', '清热解毒，凉血止血。', '1．热毒泻痢。\n2．血热出血证。\n3．湿热黄疸。\n4．热毒疮肿，毒蛇咬伤。', '', '3', '【用法用量】煎服，9～20g。鲜品30~60g。外用适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('89', '委陵菜', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'WLC', '克', '苦，寒。归肝、大肠经。', '清热解毒，凉血，止痢。', '1．热毒泻痢。\n2．血热出血。', '', '3', '【用法用量】煎服，9～15g。外用鲜品适量，煎水洗或捣烂敷患处。\n【其他】委陵菜有翻白草之别名，有些地区作翻白草入药，然正品翻白草为同科植物翻白草的干燥全草或根。二者功效大体相同，唯品种不同，以委陵菜效佳。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('90', '翻白草', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'FBC', '克', '苦，寒。归胃、大肠经。', '清热解毒，止血，止痢。', '1．湿热泻痢。\n2．痈肿疮毒。\n3．血热出血。\n4．肺热咳喘。', '委陵菜', '3', '【用法用量】煎服，9～15g。鲜品30~60g。外用适量，捣敷患处。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('91', '半边莲', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'BBL', '克', '辛，平。归心、小肠、肺经。', '清热解毒，利水消肿。', '1．疮痈肿毒，蛇虫咬伤。\n2．腹胀水肿。\n 3．湿疮湿疹。', '', '5', '【用法用量】煎服，干品10～15g，鲜品30～60g。外用适量。\n【使用注意】虚证水肿忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('92', '白花蛇舌草', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'BHSTC', '克', '微苦、甘，寒。归胃、大肠、小肠经。', '清热解毒，利湿通淋。', '1．痈肿疮毒，咽喉肿痛，毒蛇咬伤。\n2．热淋涩痛。', '', '5', '【用法用量】煎服，15～60g。外用适量。\n【使用注意】阴疽及脾胃虚寒者忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('93', '山慈菇', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'SCG', '克', '甘、微辛，凉。归肝、脾经。', '清热解毒，消痈散结。', '1．痈疽疔毒，瘰疬痰核。\n2．癥瘕痞块。', '', '5', '【用法用量】煎服，3～9g。外用适量。\n【使用注意】正虚体弱者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('94', '熊胆', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'XD', '克', '苦，寒。归肝、胆、心经。', '清热解毒，息风止痉，清肝明目。', '1．热极生风，惊痫抽搐。\n2．热毒疮痈。\n3．目赤翳障。', '', '4', '【用法用量】内服，0.25～0．5g，入丸、散，由于本品有腥苦味，口服易引起呕吐，故宜用胶囊剂。外用适量，调涂患处。\n【使用注意】脾胃虚寒者忌服。虚寒证当禁用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('95', '千里光', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'QLG', '克', '苦，寒。归肺、肝、大肠经。', '清热解毒，清肝明目。', '1．痈肿疮毒。\n2．目赤肿痛。\n3．湿热泻痢。', '', '3', '【用法用量】煎服，9~15g，鲜品30g。外用适量。\n【使用注意】脾胃虚寒者慎服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('96', '白蔹', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'BL', '克', '苦、辛，微寒。归心、胃经。', '清热解毒，消痈散结，敛疮生肌。', '1．疮痈肿毒，瘰疬痰核。\n2．水火烫伤，手足皲裂。', '', '3', '【用法用量】煎服，4.5～9g。外用适量，煎汤外洗或研成极细粉末敷于患处。\n【使用注意】脾胃虚寒者不宜服。不宜于乌头类药材同用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('97', '四季青', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'SJQ', '克', '苦、涩，寒。归肺、心经。', '清热解毒，凉血止血，敛疮。', '1．水火烫伤，湿疹，疮疡。\n2．肺热咳嗽，咽喉肿痛，热淋，泻痢。\n3．外伤出血。', '', '4', '【用法用量】煎服，15～30g。外用适量。\n【使用注意】脾胃虚寒，肠滑泄泻者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('98', '绿豆', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清热解毒'), '', 'LD', '克', '甘，寒。归心，胃经。', '清热解毒，消暑，利水。', '1．痈肿疮毒。\n2．暑热烦渴。\n3．药食中毒。\n4．水肿，小便不利。', '', '3', '【用法用量】煎服，15～30g。外用适量。\n【使用注意】脾胃虚寒，肠滑泄泻者忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('99', '生地黄', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清营凉血'), '生地', 'SDH,SD', '克', '甘、苦，寒。归心、肝、肾经。', '清热凉血，养阴生津。', '1．热入营血，舌绛烦渴、斑疹吐衄。\n2．阴虚内热，骨蒸劳热。\n3．津伤口渴，内热消渴，肠燥便秘。', '', '3', '【用法用量】煎服，10—15g。鲜品用量加倍，或以鲜品捣汁入药。\n【使用注意】脾虚湿滞，腹满便溏者不宜使用。\n');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('100', '地黄', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清营凉血'), '', 'SDH', '克', '甘、苦，寒。归心、肝、肾经。', '清热凉血，养阴生津。', '1．热入营血，舌绛烦渴、斑疹吐衄。\n2．阴虚内热，骨蒸劳热。\n3．津伤口渴，内热消渴，肠燥便秘。', '', '3', '【用法用量】煎服，10—15g。鲜品用量加倍，或以鲜品捣汁入药。\n【使用注意】脾虚湿滞，腹满便溏者不宜使用。\n');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('101', '玄参', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清营凉血'), '', 'XS,XC', '克', '甘、苦、咸，微寒。归肺、胃、肾经。', '清热凉血，泻火解毒，滋阴。', '1．温邪入营，内陷心包，温毒发斑。\n2．热病伤阴，津伤便秘，骨蒸劳嗽。\n3．目赤咽痛，瘰疬，白喉，痈肿疮毒。', '生地黄', '3', '【用法用量】煎服，10—15g。\n【使用注意】脾胃虚寒，食少便溏者不宜服用。反藜芦。\n【鉴别用药】玄参与生地黄，均能清热凉血、养阴生津，用治热入营血、热病伤阴、阴虚内热等证，常相须为用。但玄参泻火解毒力较强，故咽喉肿痛、痰火瘰疬多用；生地黄清热凉血力较大，故血热出血、内热消渴多用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('102', '牡丹皮', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清营凉血'), '丹皮', 'MDP,DP', '克', '苦、甘，微寒。归心、肝、肾经。', '清热凉血，活血祛瘀。', '1．温毒发斑，血热吐衄。\n2．温病伤阴，阴虚发热，夜热早凉、无汗骨蒸。\n3．血滞经闭、痛经、跌打伤痛。\n4．痈肿疮毒。', '', '3', '【用法用量】煎服，6—12g。清热凉血宜生用，活血祛瘀宜酒炙用。\n【使用注意】血虚有寒、月经过多及孕妇不宜用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('103', '赤芍', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清营凉血'), '', 'CS', '克', '苦、微寒。归肝经。', '清热凉血，散瘀止痛。', '1．温毒发斑，血热吐衄。\n2．目赤肿痛，痈肿疮疡。\n3．肝郁胁痛，经闭痛经，癥瘕腹痛，跌打损伤。', '', '3', '【用法用量】煎服，6-12g。\n【使用注意】血寒经闭不宜用。反藜芦。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('104', '紫草', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清营凉血'), '', 'ZC', '克', '甘、咸，寒。归心，肝经。', '清热凉血，活血，解毒透疹。', '1．温病血热毒盛，斑疹紫黑，麻疹不透。\n2．疮疡，湿疹，水火烫伤。', '', '3', '【用法用量】煎服，5—10g。外用适量，熬膏或用植物油浸泡涂搽。\n【使用注意】本品性寒而滑利，脾虚便溏者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('105', '水牛角', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清营凉血'), '', 'SNJ,SLJ', '克', '苦，寒。归心、肝经。', '清热凉血，解毒，定惊。', '1．温病高热，神昏谵语，惊风，癫狂。\n2．血热妄行斑疹、吐衄。\n3．痈肿疮疡，咽喉肿痛。', '', '3', '【用法用量】镑片或粗粉煎服，15-30g，宜先煎3小时以上。水牛角浓缩粉冲服，每次1.5-3g，每日2次。\n【使用注意】脾胃虚寒者忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('106', '青蒿', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清虚热'), '', 'QH', '克', '苦、辛，寒。归肝、胆经。', '清透虚热，凉血除蒸，解暑，截疟。', '1．温邪伤阴，夜热早凉。\n2．阴虚发热，劳热骨蒸。\n3．暑热外感，发热口渴。\n4．疟疾寒热。', '', '2', '【用法用量】煎服，6～12g，不宜久煎；或鲜用绞汁服。\n【使用注意】脾胃虚弱，肠滑泄泻者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('107', '白薇', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清虚热'), '', 'BW,BH', '克', '苦、咸，寒。归胃、肝、肾经。', '清热凉血，利尿通淋，解毒疗疮。', '1．阴虚发热，产后虚热。\n2．热淋，血淋。\n3．疮痈肿毒，毒蛇咬伤，咽喉肿痛。\n4．阴虚外感。', '', '5', '【用法用量】煎服，4.5～9g。\n【使用注意】脾胃虚寒、食少便溏者不宜服用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('108', '地骨皮', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清虚热'), '', 'DGP', '克', '甘，寒。归肺、肝、肾经。', '凉血除蒸，清肺降火。', '1．阴虚发热，盗汗骨蒸。\n2．肺热咳嗽。\n3．血热出血证。', '', '3', '【用法用量】煎服，9～15g。\n【使用注意】外感风寒发热及脾虚便溏者不宜用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('109', '银柴胡', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清虚热'), '', 'YCH', '克', '甘，微寒。归肝、胃经。', '清虚热，除疳热。', '1．阴虚发热。\n2．疳积发热。', '', '3', '【用法用量】煎服，3～9g。\n【使用注意】外感风寒，血虚无热者忌用。\n【鉴别用药】银柴胡与柴胡，名称相似且均有退热之功。然银柴胡能清虚热，除疳热，尤善治疗阴虚发热、小儿疳热；而柴胡能发表退热，善治外感发热、邪在少阳之往来寒热');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('110', '胡黄连', (select PK_ID from SYNDROME_SUBJECT where NAME = '__清虚热'), '', 'HHL', '克', '苦，寒。归肝、胃、大肠经。', '退虚热，除疳热，清湿热。', '1．骨蒸潮热。\n2．小儿疳热。\n3．湿热泻痢。', '', '3', '【用法用量】煎服，1.5～9g。\n【使用注意】脾胃虚寒者慎用。\n【鉴别用药】胡黄连与黄连，名称相似且均为苦寒清热燥湿之品，善除胃肠湿热，同为治湿热泻痢之良药。然胡黄连善退虚热、除疳热；而黄连则善清心火、泻胃火，为解毒要药。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('111', '大黄', (select PK_ID from SYNDROME_SUBJECT where NAME = '__寒下'), '', 'DH', '克', '苦，寒。归脾、胃、大肠、肝、心包经。', '泻下攻积，清热泻火，凉血解毒，逐瘀通经。', '1．积滞便秘。\n2．血热吐衄，目赤咽肿。\n3．热毒疮疡，烧烫伤。\n4．瘀血证。\n5．湿热痢疾、黄疸、淋证。', '', '3', '【用法用量】煎服，5～15g；入汤剂应后下，或用开水泡服。外用适量。\n【使用注意】本品为峻烈攻下之品，易伤正气，如非实证，不宜妄用；本品苦寒，易伤胃气，脾胃虚弱者慎用；其性沉降，且善活血祛瘀，故妇女怀孕、月经期、哺乳期应忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('112', '芒硝', (select PK_ID from SYNDROME_SUBJECT where NAME = '__寒下'), '', 'MX', '克', '咸、苦，寒。归胃、大肠经。', '泻下攻积，润燥软坚，清热消肿。', '1．积滞便秘。\n2．咽痛、口疮、目赤及痈疮肿痛。', '', '3', '【用法用量】10～15g，冲入药汁内或开水溶化后服。外用适量。\n【使用注意】孕妇及哺乳期妇女忌用或慎用。\n【鉴别用药】芒硝、大黄均为泻下药，常相须用治肠燥便秘。然大黄味苦泻下力强，有荡涤肠胃之功，为治热结便秘之主药；芒硝味咸，可软坚泻下，善除燥屎坚结。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('113', '番泻叶', (select PK_ID from SYNDROME_SUBJECT where NAME = '__寒下'), '', 'FXY', '克', '甘、苦，寒。归大肠经。', '泻下通便。', '1．热结便秘。\n2．腹水肿胀。', '', '4', '【用法用量】温开水泡服，1.5～3g；煎服，2～6g，宜后下。\n【使用注意】妇女哺乳期、月经期及孕妇忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('114', '芦荟', (select PK_ID from SYNDROME_SUBJECT where NAME = '__寒下'), '', 'LH,LK', '克', '苦，寒。归肝、胃、大肠经。', '泻下通便，清肝，杀虫。', '1．热结便秘。\n2．烦燥惊痫。\n3．小儿疳积。', '', '5', '【用法用量】入丸、散服，每次1～2g。外用适量。\n【使用注意】脾胃虚弱，食少便溏及孕妇忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('115', '火麻仁', (select PK_ID from SYNDROME_SUBJECT where NAME = '__润下'), '麻仁,麻子,麻子仁,大麻仁', 'HMR,MR,MZ,MZR,DMR', '克', '甘，平。归脾、胃、大肠经。', '润肠通便。', '肠燥便秘。', '', '3', '【用法用量】煎服，10～15g。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('116', '郁李仁', (select PK_ID from SYNDROME_SUBJECT where NAME = '__润下'), '', 'YLR', '克', '辛、苦、甘，平。归脾、大肠、小肠经。', '润肠通便，利水消肿。', '1．肠燥便秘。\n2．水肿胀满及脚气浮肿。', '', '3', '【用法用量】煎服，6～12g。\n【使用注意】孕妇慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('117', '松子仁', (select PK_ID from SYNDROME_SUBJECT where NAME = '__润下'), '', 'SZR', '克', '甘，温。归肺、肝、大肠经。', '润肠通便，润肺止咳。', '1．肠燥便秘。\n2．肺燥干咳。', '', '3', '【用法用量】煎服，5～10g。或入膏、丸。\n【使用注意】脾虚便溏，湿痰者禁用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('118', '甘遂', (select PK_ID from SYNDROME_SUBJECT where NAME = '__逐水'), '', 'GS', '克', '苦，寒。有毒。归肺、肾、大肠经。', '泻水逐饮，消肿散结。', '1．水肿，臌胀，胸胁停饮。\n2．风痰癫痫。本品尚有逐痰涎作用。\n3．疮痈肿毒。', '', '8', '【用法用量】入丸、散服，每次0.5～1g。外用适量，生用。内服醋制用，以减低毒性。\n【使用注意】虚弱者及孕妇忌用。不宜与甘草同用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('119', '京大戟', (select PK_ID from SYNDROME_SUBJECT where NAME = '__逐水'), '大戟', 'JDJ,JDG,DJ', '克', '苦，寒。有毒。归肺、脾、肾经。', '泻水逐饮，消肿散结。', '1．水肿、臌胀、胸胁停饮。\n2．痈肿疮毒，瘰疬痰核。', '甘遂', '7', '【用法用量】煎服，1.5～3g；入丸、散服，每次1g。外用适量，生用。内服醋制用，以减低毒性。\n【使用注意】虚弱者及孕妇忌用。不宜与甘草同用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('120', '芫花', (select PK_ID from SYNDROME_SUBJECT where NAME = '__逐水'), '', 'YH', '克', '苦、辛，温。有毒。归肺、脾、肾经。', '泻水逐饮，祛痰止咳，杀虫疗疮。', '1．胸胁停饮，水肿，臌胀。\n2．咳嗽痰喘。\n3．头疮、白秃、顽癣及痈肿。', '京大戟', '6', '【用法用量】煎服，1.5～3g；入丸、散服，每次0.6g。外用适量。内服醋制用，以降低毒性。\n【使用注意】虚弱者及孕妇忌用。不宜与甘草同用。\n【鉴别用药】甘遂、京大戟、芫花均为峻下逐水药，具有泻水逐饮之效，作用峻猛，常同用治疗水肿、臌胀、胸胁停饮之证。但甘遂作用最强，其次为京大戟，最弱者为芫花。其中甘遂善行经隧之水湿，大戟偏行脏腑水湿，芫花以泻胸胁水饮，并祛痰止咳见长。另外，三者均有毒，且不宜与甘草同用；内服时，多醋制，可降低其毒性。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('121', '商陆', (select PK_ID from SYNDROME_SUBJECT where NAME = '__逐水'), '', 'SL', '克', '苦，寒。有毒。归肺、脾、肾、大肠经。', '泻下逐水，消肿散结。', '1．水肿，臌胀。\n2．疮痈肿毒。', '', '8', '【用法用量】煎服，5～10g。醋制以降低毒性。外用适量。\n【使用注意】孕妇忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('122', '牵牛子', (select PK_ID from SYNDROME_SUBJECT where NAME = '__逐水'), '黑牵牛,牵牛', 'QNZ,QLZ,HQN,HQL,QN,QL', '克', '苦，寒。有毒。归肺、肾大肠经。', '泻下逐水，去积杀虫。', '1．水肿，臌胀。\n2．痰饮喘咳。\n3．虫积腹痛。', '', '8', '【用法用量】煎服，3～9g。入丸、散服，每次1.5～3g。本品炒用药性减缓。\n【使用注意】孕妇忌用。不宜与巴豆、巴豆霜同用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('123', '巴豆', (select PK_ID from SYNDROME_SUBJECT where NAME = '__逐水'), '', 'BD', '克', '辛，热；有大毒。归胃、大肠经。', '峻下冷积，逐水退肿，祛痰利咽，外用蚀疮。', '1．寒积便秘。\n2．腹水臌胀。\n3．喉痹痰阻。\n4．痈肿未溃、疥癣恶疮。', '', '9', '【用法用量】入丸、散服，每次0.1～0.3g.。大多数制成巴豆霜用，以减低毒性。外用适量。\n【使用注意】孕妇及体弱者忌用。不宜与牵牛子同用。\n【鉴别用药】巴豆辛热燥烈，要力刚猛，峻下冷积，开通闭塞，主治冷积便秘重症；大黄苦寒泄降，峻下实热，荡涤胃肠，主治实热积滞便秘急症。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('124', '千金子', (select PK_ID from SYNDROME_SUBJECT where NAME = '__逐水'), '', 'QJZ', '克', '辛，温。有毒。归肝、肾、大肠经。', '逐水消肿，破血消癥。', '1．水肿、臌胀。\n2．癥瘕、经闭。', '', '6', '【用法用量】1～2g；去壳，去油用，多入丸、散服。外用适量，捣烂敷患处。\n【使用注意】孕妇及体弱便溏者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('125', '独活', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'DH', '克', '辛、苦，微温。归肾、膀胱经。', '袪风湿，止痛，解表。', '1．风寒湿痹。\n2．风寒挟湿表证。\n3．少阴头痛。', '', '3', '【用法用量】煎服，3～9g。外用，适量。\n【鉴别用药】羌活与独活，均能袪风湿，止痛，解表，以治风寒湿痹，风寒挟湿表证，头痛。但羌活性较燥烈，发散力强，常用于风寒湿痹，痛在上半身者，治头痛因于风寒者；独活性较缓和，发散力较羌活为弱，多用于风寒湿痹在下半身者，治头痛属少阴者。若风寒湿痹，一身尽痛，两者常配伍应用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('126', '威灵仙', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'WLX', '克', '辛、咸，温。归膀胱经。', '袪风湿，通络止痛，消骨哽。', '1．风湿痹证。\n2．骨哽咽喉。', '', '3', '【用法用量】煎服，6～9g。外用，适量。\n【使用注意】本品辛散走窜，气血虚弱者慎服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('127', '川乌', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '乌头', 'CW,WT', '克', '辛、苦，热。有大毒。归心、肝、肾、脾经。', '袪风湿，温经止痛。', '1．风寒湿痹。\n2．心腹冷痛，寒疝疼痛。\n3．跌打损伤，麻醉止痛。', '', '8', '【用法用量】煎服，1.5～3g；宜先煎、久煎。外用，适量。\n【使用注意】孕妇忌用；不宜与贝母类、半夏、白及、白蔹、天花粉、瓜蒌类同用；内服一般应炮制用，生品内服宜慎；酒浸、酒煎服易致中毒，应慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('128', '草乌', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '乌头,五毒根', 'CW,WT,WDG', '克', '辛、苦，热；有大毒。归心、肝、肾、脾经。', '祛风除湿，温经止痛。', '用于风寒湿痹，关节疼痛，心腹冷痛，寒疝作痛，麻醉止痛。', '', '8', '【用法用量】一般炮制后用。\n【注意】生品内服宜慎。不宜与贝母、半夏、白及、白蔹、天花粉、瓜蒌同用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('129', '蕲蛇', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'QS', '克', '甘、咸，温。有毒。归肝经。', '袪风，通络，止痉。', '1．风湿顽痹，中风半身不遂。\n2．小儿惊风，破伤风。\n3．麻风，疥癣。', '', '6', '【用法用量】煎汤，3～9g；研末吞服，一次1～1.5g，一日2～3次。或酒浸、熬膏、入丸、散服。\n【使用注意】阴虚内热者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('130', '乌梢蛇', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'WSS', '克', '甘，平。归肝经。', '袪风，通络，止痉。', '1．风湿顽痹，中风半身不遂。\n2．小儿惊风，破伤风。\n3．麻风，疥癣。', '', '3', '【用法用量】煎服，9～12g；研末，每次2～3g；或入丸剂、酒浸服。外用，适量。\n【使用注意】血虚生风者慎服。\n【鉴别用药】蕲蛇、金钱白花蛇、乌梢蛇性皆走窜，均能袪风，通络，止痉，凡内外风毒壅滞之证皆宜，尤以善治病久邪深者为其特点。其作用以金钱白花蛇最强，蕲蛇次之，乌梢蛇最弱；且金钱白花蛇与蕲蛇均有毒性偏温燥，而乌梢蛇性平无毒力较缓。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('131', '木瓜', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'MG', '克', '酸，温。归肝、脾经。', '舒筋活络，和胃化湿。', '1．风湿痹证。\n2．脚气水肿。\n3．吐泻转筋。', '', '3', '【用法用量】煎服，6～9g。\n【使用注意】内有郁热，小便短赤者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('132', '蚕沙', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'CS', '克', '甘、辛，温。归肝、脾、胃经。', '袪风湿，和胃化湿。', '1．风湿痹证。\n2．吐泻转筋。\n3．风疹湿疹瘙痒。', '', '3', '【用法用量】煎服，5～15g；宜布包入煎。外用，适量。\n【鉴别用药】蚕沙与木瓜均能袪风湿、和胃化湿，以治湿痹拘挛及湿阻中焦之吐泻转筋。但蚕沙作用较缓，又善袪风，故凡风湿痹痛，不论风重、湿重均可应用；木瓜善舒筋活络，长于治筋脉拘挛，除常用于湿阻中焦吐泻转筋外，也可用于血虚肝旺，筋脉失养，挛急疼痛等。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('133', '伸筋草', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'SJC', '克', '微苦、辛，温。归肝、脾、肾经。', '袪风湿，舒筋活络。', '1．风寒湿痹，肢软麻木。\n2．跌打损伤。', '', '4', '【用法用量】煎服，3～12g。外用，适量。\n【使用注意】孕妇慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('134', '寻骨风', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'XGF', '克', '辛，苦，平。归肝经。', '袪风湿，通络止痛。', '1．风湿痹证。\n2．跌打损伤。', '', '3', '【用法用量】煎服，10～15g。外用，适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('135', '松节', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'SJ', '克', '苦、辛，温。归肝、肾经。', '祛风湿，通络止痛。', '1．风寒湿痹。\n2．跌打损伤。', '', '3', '【用法用量】煎服，10～15g。外用，适量。\n【使用注意】阴虚血燥者慎服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('136', '海风藤', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'HFT', '克', '辛、苦，微温。归肝经。', '袪风湿，通络止痛。', '1．风寒湿痹。\n2．跌打损伤。', '', '3', '【用法用量】煎服，6～12g。外用，适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('137', '青风藤', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'QFT', '克', '苦、辛，平。归肝、脾经。', '袪风湿，通经络，利小便。', '1．风湿痹证。\n2．水肿，脚气。', '', '4', '【用法用量】煎服，6～12g。外用，适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('138', '丁公藤', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'DGT', '克', '辛，温。有小毒。归肝、脾、胃经。', '祛风湿，消肿止痛。', '1．风湿痹痛，半身不遂。\n2．跌打损伤。', '', '5', '【用法用量】煎服，3～6g；或配制酒剂，内服或外搽。\n【使用注意】本品有强烈的发汗作用，虚弱者慎用，孕妇忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('139', '昆明山海棠', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'KMSHT', '克', '苦、辛，温。有大毒。归肝、脾、肾经。', '祛风湿，祛瘀通络，续筋接骨。', '1．风湿痹证。\n2．跌打损伤，骨折。', '', '8', '【用法用量】煎服，根6～15g，茎枝20～30g，宜先煎。或酒浸服。外用，适量。\n【使用注意】孕妇及体弱者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('140', '雪上一枝蒿', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'XSYZH', '克', '苦、辛，温。有大毒。归肝经。', '袪风湿，活血止痛。', '1．疼痛证。\n2．疮疡肿毒，虫蛇咬伤。', '', '8', '【用法用量】研末服，0.02～0.04g。外用,适量。\n【使用注意】内服须经炮制并严格控制剂量，孕妇、老弱、小儿及心脏病、溃疡病患者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('141', '路路通', (select PK_ID from SYNDROME_SUBJECT where NAME = '__疏散外风'), '', 'LLT', '克', '苦，平。归肝、肾经。', '袪风活络，利水，通经。', '1．风湿痹痛，中风半身不遂。\n2．跌打损伤。\n3．水肿。\n4．经行不畅，经闭。\n5．乳少，乳汁不通。', '', '3', '【用法用量】煎服，5～9g。外用，适量。\n【使用注意】月经过多及孕妇忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('142', '秦艽', (select PK_ID from SYNDROME_SUBJECT where NAME = '__平熄内风'), '', 'QJ', '克', '辛、苦，平。归胃、肝、胆经。', '袪风湿，通络止痛，退虚热，清湿热。', '1．风湿痹证。\n2．中风不遂。\n3．骨蒸潮热，疳积发热。\n4．湿热黄疸。', '', '3', '【用法用量】煎服，3～9g。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('143', '防己', (select PK_ID from SYNDROME_SUBJECT where NAME = '平熄内风'), '', 'FJ', '克', '苦、辛，寒。归膀胱、肺经。', '袪风湿，止痛，利水消肿。', '1．风湿痹证。\n2．水肿，小便不利，脚气。\n3．湿疹疮毒。', '', '4', '【用法用量】煎服，4.5～9g。\n【使用注意】本品大苦大寒易伤胃气，胃纳不佳及阴虚体弱者慎服。\n【鉴别用药】汉防己与木防己均有袪风湿、利水之功。但汉防己偏于利水消肿，木防己偏于袪风湿止痛；若症偏于下部，湿重于风者，多用汉防己；症偏于上部，风重于湿者，多用木防己。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('144', '桑枝', (select PK_ID from SYNDROME_SUBJECT where NAME = '平熄内风'), '', 'SZ', '克', '微苦，平。归肝经。', '袪风湿，利关节。', '风湿痹证。', '', '3', '【用法用量】煎服，9～15g。外用，适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('145', '豨莶草', (select PK_ID from SYNDROME_SUBJECT where NAME = '平熄内风'), '', 'XXC,XQC', '克', '辛、苦，寒。归肝、肾经。', '袪风湿，利关节，解毒。', '1．风湿痹痛，中风半身不遂。\n2．风疹，湿疮，疮痈。', '', '3', '【用法用量】煎服，9～12g。外用，适量。治风湿痹痛、半身不遂宜制用，治风疹湿疮、疮痈宜生用。\n【鉴别用药】豨莶草能袪风湿，通经络，利关节。生用性寒，善清热解毒，化湿热，除风痒，故宜于风湿热痹，关节红肿热痛以及湿热疮疡、风疹、湿毒瘙痒等证；酒蒸制后转为甘温，袪风除湿之中寓有补益肝肾之功，故可用于风湿四肢麻痹，筋骨疼痛，腰膝酸软及中风半身不遂等证，但单用作用缓慢，久服方效。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('146', '臭梧桐', (select PK_ID from SYNDROME_SUBJECT where NAME = '平熄内风'), '', 'CWT', '克', '辛、苦、甘，凉。归肝经。', '袪风湿，通经络，平肝。', '1．风湿痹证。\n2．风疹，湿疮。\n3．肝阳上亢，头痛眩晕。', '', '3', '【用法用量】煎服，5～15g；研末服，每次3g。外用，适量。用于高血压病不宜久煎。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('147', '海桐皮', (select PK_ID from SYNDROME_SUBJECT where NAME = '平熄内风'), '', 'HTP', '克', '苦、辛，平。归肝经。', '祛风湿，通络止痛，杀虫止痒。', '1．风湿痹证。\n2．疥癣，湿疹。', '', '3', '【用法用量】煎服，5～15g；或酒浸服。外用，适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('148', '络石藤', (select PK_ID from SYNDROME_SUBJECT where NAME = '平熄内风'), '', 'LST', '克', '苦，微寒。归心、肝、肾经。', '袪风通络，凉血消肿。', '1．风湿热痹。\n2．喉痹，痈肿。\n3．跌扑损伤。', '', '3', '【用法用量】煎服，6～12g。外用，适量，鲜品捣敷。\n【鉴别用药】络石藤与海风藤均能袪风通络，常用于风湿所致的关节屈伸不利，筋脉拘挛及跌打损伤。但海风藤性微温，适用于风寒湿痹，肢节疼痛，筋脉拘挛，屈伸不利者；络石藤性微寒，尤宜于风湿热痹，筋脉拘挛，腰膝酸痛者。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('149', '雷公藤', (select PK_ID from SYNDROME_SUBJECT where NAME = '平熄内风'), '', 'LGT', '克', '苦、辛，寒。有大毒。归肝、肾经。', '祛风湿，活血通络，消肿止痛，杀虫解毒。', '1．风湿顽痹。\n2．麻风、顽癣、湿疹、疥疮、皮炎、皮疹。\n3．疔疮肿毒。', '', '8', '【用法用量】煎汤，10～25g(带根皮者减量)，文火煎1～2小时；研粉，每日1.5～4.5g。外用，适量。\n【使用注意】内脏有器质性病变及白细胞减少者慎服；孕妇忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('150', '老鹳草', (select PK_ID from SYNDROME_SUBJECT where NAME = '平熄内风'), '', 'LGC,LHC', '克', '辛、苦，平。归肝、肾、脾经。', '袪风湿，通经络，清热毒，止泻痢。', '1．风湿痹证。\n2．泄泻痢疾。\n3．疮疡。', '', '3', '【用法用量】煎服，9～15g；或熬膏、酒浸服。外用，适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('151', '穿山龙', (select PK_ID from SYNDROME_SUBJECT where NAME = '平熄内风'), '', 'CSL', '克', '苦，微寒。归肝、肺经。', '袪风湿，活血通络，清肺化痰。', '1．风湿痹证。\n2．痰热咳喘。', '', '5', '【用法用量】煎服，10～15g；或酒浸服。外用，适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('152', '丝瓜络', (select PK_ID from SYNDROME_SUBJECT where NAME = '平熄内风'), '', 'SGL', '克', '甘，平。归肺、胃、肝经。', '袪风，通络，活血。', '1．风湿痹证。\n2．胸胁胀痛。\n3．乳汁不通，乳痈。', '', '3', '【用法用量】煎服，4.5～9g。外用，适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('153', '五加皮', (select PK_ID from SYNDROME_SUBJECT where NAME = '治风'), '', 'WJP', '克', '辛、苦，温。归肝、肾经。', '袪风湿，补肝肾，强筋骨，利水。', '1．风湿痹证。\n2．筋骨痿软，小儿行迟，体虚乏力。\n3．水肿，脚气。', '', '4', '【用法用量】煎服，4.5～9g；或酒浸、入丸、散服。\n【使用注意】有南五加皮和北五加皮之分。南五加皮与北五加皮科属不同，功效有异，且北五加皮有毒，不应混用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('154', '桑寄生', (select PK_ID from SYNDROME_SUBJECT where NAME = '治风'), '', 'SJS', '克', '苦、甘，平。归肝、肾经。', '袪风湿，补肝肾，强筋骨，安胎。', '1．风湿痹证。\n2．崩漏经多，妊娠漏血，胎动不安。', '', '3', '【用法用量】煎服，9～15g。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('155', '狗脊', (select PK_ID from SYNDROME_SUBJECT where NAME = '治风'), '', 'GJ', '克', '苦、甘，温。归肝、肾经。', '袪风湿，补肝肾，强腰膝。', '1．风湿痹证。\n2．腰膝酸软，下肢无力。\n3．遗尿，白带过多。', '', '3', '【用法用量】煎服，6～12g。\n【使用注意】肾虚有热，小便不利，或短涩黄赤者慎服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('156', '千年健', (select PK_ID from SYNDROME_SUBJECT where NAME = '治风'), '', 'QNJ', '克', '苦、辛，温。归肝、肾经。', '袪风湿，强筋骨。', '风寒湿痹。', '', '3', '【用法用量】煎服，4.5～9g；或酒浸服。\n【使用注意】阴虚内热者慎服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('157', '雪莲花', (select PK_ID from SYNDROME_SUBJECT where NAME = '治风'), '', 'XLH', '克', '甘、微苦，温。归肝、肾经。', '袪风湿，强筋骨，补肾阳，调经止血。', '1．风湿痹证。\n2．阳痿。\n3．月经不调，经闭痛经，崩漏带下。', '', '3', '【用法用量】煎服，6～12g。外用，适量。\n【使用注意】孕妇忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('158', '鹿衔草', (select PK_ID from SYNDROME_SUBJECT where NAME = '治风'), '', 'LXC', '克', '甘、苦，温。归肝、肾经。', '袪风湿，强筋骨，止血。', '1．风湿痹证。\n2．月经过多，崩漏，咯血，外伤出血。\n3．久咳劳嗽。', '', '3', '【用法用量】煎服，9～15g。外用，适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('159', '石楠叶', (select PK_ID from SYNDROME_SUBJECT where NAME = '治风'), '', 'SNY', '克', '辛、苦，平。有小毒。归肝、肾经。', '祛风湿，通经络，益肾气。', '1．风湿痹证。\n2．头风头痛。\n3．风疹瘙痒。', '', '3', '【用法用量】煎服，10～15g。外用，适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('160', '藿香', (select PK_ID from SYNDROME_SUBJECT where NAME = '祛湿'), '', 'HX', '克', '辛，微温。归脾、胃、肺经。', '化湿，止呕，解暑。', '1．湿阻中焦。\n2．呕吐。\n3．暑湿、湿温。', '', '3', '【用法用量】煎服，5～10g。鲜品加倍。\n【使用注意】阴虚血燥者不宜用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('161', '佩兰', (select PK_ID from SYNDROME_SUBJECT where NAME = '祛湿'), '', 'PL', '克', '辛，平。归脾、胃、肺经。', '化湿，解暑。', '1．湿阻中焦。\n2．暑湿、湿温。', '', '3', '【用法用量】煎服，5～10g。鲜品加倍。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('162', '苍术', (select PK_ID from SYNDROME_SUBJECT where NAME = '祛湿'), '', 'CS', '克', '辛，苦，温。归脾、胃、肝经。', '燥湿健脾，祛风散寒。', '1．湿阻中焦证。\n2．风湿痹证。\n3．风寒挟湿表证。', '', '3', '【用法用量】煎服，5～10g。\n【使用注意】阴虚内热，气虚多汗者忌用。\n【鉴别用药】苍术、藿香、佩兰均为芳香化湿药，具有化湿之力，用于湿阻中焦证。但苍术苦温燥烈，可燥湿健脾，不仅适用于湿阻中焦，亦可用于其它湿邪泛滥之症；而藿香、佩兰性微温或平，以化湿醒脾为主，多用于湿邪困脾之症。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('163', '厚朴', (select PK_ID from SYNDROME_SUBJECT where NAME = '祛湿'), '', 'HP', '克', '苦、辛，温。归脾、胃、肺、大肠经。', '燥湿消痰，下气除满。', '1．湿阻中焦，脘腹胀满。\n2．食积气滞，腹胀便秘。\n3．痰饮喘咳。', '苍术', '3', '【用法用量】煎服，3～10g。或入丸、散。\n【使用注意】本品辛苦温燥湿，易耗气伤津，故气虚津亏者及孕妇当慎用。\n【鉴别用药】厚朴、苍术均为化湿药，性能辛苦温，具有燥湿之功，常相须为用，治疗湿阻中焦之证。但厚朴以苦味为重，苦降下气消积除胀满，又下气消痰平喘，既可除无形之湿药，又可消有形之实满，为消除胀满的要药；而苍术辛散温燥为主，为治湿阻中焦之要药，又可祛风湿。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('164', '砂仁', (select PK_ID from SYNDROME_SUBJECT where NAME = '祛湿'), '', 'SR', '克', '辛，温。归脾、胃、肾经。', '化湿行气，温中止泻，安胎。', '1．湿阻中焦及脾胃气滞证。\n2．脾胃虚寒吐泻。\n3．气滞妊娠恶阻及胎动不安。', '', '3', '【用法用量】煎服，3～6g，入汤剂宜后下。\n【使用注意】阴虚血燥者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('165', '豆蔻', (select PK_ID from SYNDROME_SUBJECT where NAME = '祛湿'), '', 'DK', '克', '辛，温。归肺、脾、胃经。', '化湿行气，温中止呕。', '1．湿阻中焦及脾胃气滞证。\n2．呕吐。', '砂仁', '3', '【用法用量】煎服，3～6g，入汤剂宜后下。\n【使用注意】阴虚血燥者慎用。\n【鉴别用药】豆蔻、砂仁同为化湿药，具有化湿行气，温中止呕、止泻之功，常相须为用，用治湿阻中焦及脾胃气滞证。但豆蔻化湿行气之力偏中上焦，而砂仁偏中下焦。故豆蔻临床上可用于湿温痞闷，温中偏胃而善止呕；砂仁化湿行气力略胜，温中重在脾而善止泻。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('166', '草豆蔻', (select PK_ID from SYNDROME_SUBJECT where NAME = '祛湿'), '', 'CDK', '克', '辛，温。归脾、胃经。', '燥湿行气，温中止呕。', '1．寒湿中阻证。\n2．寒湿呕吐。', '', '3', '【用法用量】煎服，3～6g。入散剂较佳。入汤剂宜后下。\n【使用注意】阴虚血燥者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('167', '草果', (select PK_ID from SYNDROME_SUBJECT where NAME = '祛湿'), '', 'CG', '克', '辛，温。归脾、胃经。', '燥湿温中，除痰截疟。', '1．寒湿中阻证。\n2．疟疾。', '', '3', '【用法用量】煎服，3～6g。\n【使用用量】阴虚血燥者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('168', '茯苓', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'FL,FN', '克', '甘、淡，平。归心、脾、肾经。', '利水消肿，渗湿，健脾，宁心。', '1．水肿。\n2．痰饮。\n3．脾虚泄泻。\n4．心悸，失眠。', '', '3', '【用法用量】煎服，9～15g。\n【使用注意】虚寒精滑者忌服。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('169', '薏苡仁', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'YYR', '克', '甘、淡，凉。归脾、胃、肺经。', '利水消肿，渗湿，健脾，除痹，清热排脓。', '1．水肿，小便不利，脚气。\n2．脾虚泄泻。\n3．湿痹拘挛。\n4．肺痈，肠痈。', '茯苓', '3', '【用法用量】煎服，9～30g。清利湿热宜生用，健脾止泻宜炒用。\n【使用注意】津液不足者慎用。\n【鉴别用药】薏苡仁与茯苓：功能相近，均利水消肿，渗湿，健脾。然薏苡仁性凉而清热，排脓消痈，又擅除痹。而茯苓性平，且补益心脾，宁心安神。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('170', '猪苓', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'ZL', '克', '甘、淡，平。归肾、膀胱经。', '利水消肿，渗湿。', '水肿，小便不利，泄泻。', '茯苓', '3', '【用法用量】煎服，6～12 g。\n【鉴别用药】猪苓与茯苓：均利水消肿，渗湿，用治水肿，小便不利等证。然猪苓利水作用较强，无补益之功。而茯苓性平和，能补能利，既善渗泄水湿，又能健脾宁心。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('171', '泽泻', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'ZX', '克', '甘，寒。归肾、膀胱经。', '利水消肿，渗湿，泄热。', '1．水肿，小便不利，泄泻。\n2．淋证，遗精。', '', '3', '【用法用量】煎服,5～10g。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('172', '冬瓜皮', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'DGP', '克', '甘，凉。归脾、小肠经。', '利水消肿，清热解暑。', '1．水肿。\n2．暑热证。', '', '3', '【用法用量】煎服，15～30g。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('173', '玉米须', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'YMX', '克', '甘，平。归膀胱、肝、胆经。', '利水消肿，利湿退黄。', '1．水肿。\n2．黄疸。', '', '3', '【用法用量】煎服，30～60g。鲜者加倍。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('174', '葫芦', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'HL', '克', '甘，平。归肺、肾经。', '利水消肿。', '1．水肿。\n2．本品利水而通淋，配伍滑石、木通、车前子等，用于热淋；配萹蓄、白茅根、小蓟等，可用于血淋。', '', '3', '【用法用量】煎服,15～30g。鲜者加倍。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('175', '香加皮', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'XJP', '克', '辛、苦，温。有毒。归肝、肾、心经。', '利水消肿，祛风湿，强筋骨。', '1．水肿，小便不利。\n2．风湿痹证。', '', '6', '【用法用量】煎服，3～6g。浸酒或入丸、散，酌量。\n【使用注意】本品有毒，服用不宜过量。\n【鉴别用药】五加科植物细柱五加的根皮，为五加皮，习称”南五加皮”。萝藦科植物杠柳的根皮，为香加皮，习称“北五加皮”。 两者均能祛风湿，强筋骨。但南、北五加皮，科属不同，功效也有不同。南五加皮无毒，祛风湿、补肝肾，强筋骨作用较好；北五加皮有强心利尿作用，有毒，故两药临床不可混用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('176', '枳椇子', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'ZJZ', '克', '甘、酸，平。归脾经。', '利水消肿，解酒毒。', '1．水肿证。\n2．酒醉。', '', '3', '【用法用量】煎服，10～15g。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('177', '泽漆', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'ZQ', '克', '辛、苦，微寒。有毒。归大肠、小肠、肺经。', '利水消肿，化痰止咳，解毒散结。', '1．水肿证。\n2．咳喘证。\n3．瘰疬，癣疮。', '', '5', '【用法用量】煎服，5～10g。外用适量。\n【使用注意】本品苦寒降泄，易伤脾胃，脾胃虚寒者及孕妇慎用。本品有毒，不宜过量或长期使用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('178', '蝼蛄', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'LG,LK', '克', '咸，寒。归膀胱、大肠、小肠经。', '利水消肿，通淋。', '1．水肿证。\n2．淋证。', '', '3', '【用法用量】煎服，6～9g。研末服，每次3～5g。外用适量。\n【使用注意】本品下行，通利之功较强，气虚体弱者及孕妇忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('179', '荠菜', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'JC,QC', '克', '甘，凉。归肝、胃经。', '利水消肿，明目，止血。', '1．水肿。\n2．肝热目赤，目生翳膜。\n3．血热出血证。', '', '3', '【用法用量】煎服，15～30g。鲜品加倍。外用适量。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('180', '车前子', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'CQZ', '克', '甘，微寒。归肝、肾、肺、小肠经。', '利尿通淋，渗湿止泻，明目，祛痰。', '1．淋证，水肿。\n2．泄泻。\n3．目赤肿痛，目暗昏花，翳障。\n4．痰热咳嗽。', '', '3', '【用法用量】煎服，9～15g。宜包煎。\n【使用注意】肾虚遗滑者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('181', '滑石', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'HS', '克', '甘、淡，寒。归膀胱、肺、胃经。', '利尿通淋，清热解暑，收湿敛疮。', '1．热淋，石淋，尿热涩痛。\n2．暑湿，湿温。\n3．湿疮，湿疹，痱子。', '', '4', '【用法用量】煎服，10～20g。宜包煎。外用适量。\n【使用注意】脾虚、热病伤津及孕妇忌用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('182', '木通', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'MT', '克', '苦，寒。有毒。归心、小肠、膀胱经。', '利尿通淋，清心火，通经下乳。', '1．热淋涩痛，水肿。\n2．口舌生疮，心烦尿赤。\n3．经闭乳少。', '', '4', '【用法用量】煎服,3～6g。\n【使用注意】本品不宜过量服或久服，孕妇忌服，内无湿热者、儿童与年老体弱者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('183', '通草', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'TC', '克', '甘、淡，微寒。归肺、胃经。', '利尿通淋，通气下乳。', '1．淋证，水肿。\n2．产后乳汁不下。', '', '3', '【用法用量】煎服,3～5g。\n【使用注意】孕妇慎用。');
