insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('185', '萹蓄', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'BX', '克', '苦，微寒。归膀胱经。', '利尿通淋，杀虫止痒。', '1．淋证。\n2．虫证，湿疹，阴痒。', '', '3', '【用法用量】煎服，9～15g。鲜者加倍。外用适量。\n【使用注意】脾虚者慎用。');
insert into MEDICINE (PK_ID, NAME, SYNDROME_ID, ALIAS, KEY_CODES, UNIT, PROPERTY, EFFICACY, INDICATION, SYNERGIST, POISON, DESCRIPTION) values ('186', '地肤子', (select PK_ID from SYNDROME_SUBJECT where NAME = '__利水渗湿'), '', 'DFZ', '克', '辛、苦，寒。归肾、膀胱经。', '利尿通淋，清热利湿，止痒。', '1．淋证。\n2．阴痒带下，风疹，湿疹。', '', '3', '【用法用量】煎服，9～15g。外用适量。');
