package irdc.Ex03_13; import android.app.Activity; /*必需引用graphics.Color才能使用Color.*的对象*/ import android.graphics.Color; import android.os.Bundle; import android.view.View; /*必需引用 widget.Button才能声明使用Button对象*/ import android.widget.Button; /*必需引用 widget.TextView才能声明使用TestView对象*/ import android.widget.TextView; public class Ex03_13 extends Activity { private Button mButton; private TextView mText; private int[] mColors; private int colornum; /** Called when the activity is first created. */ @Override public void onCreate(Bundle savedInstanceState) { super.onCreate(savedInstanceState); setContentView(R.layout.main); /*透过findViewById建构子来使用main.xml与string.xml 中button与textView的参数*/ mButton=(Button) findViewById(R.id.mybutton); mText= (TextView) findViewById(R.id.mytext); /*声明并建构一整数array来储存欲使用的文字颜色*/ mColors = new int[] { Color.BLACK, Color.RED, Color.BLUE, Color.GREEN, Color.MAGENTA, Color.YELLOW }; colornum=0; /*使用setOnClickListener让按钮监听事件*/ mButton.setOnClickListener(new View.OnClickListener() { /*使用onClick让用户点下按钮来驱动变动文字颜色*/ public void onClick(View v) { if (colornum < mColors.length) { mText.setTextColor(mColors[colornum]); colornum++; } else colornum=0; } } ); } }