package irdc.ex05_24; /* import相关class */ import java.util.ArrayList; import java.util.List; import android.app.ListActivity; import android.content.ContentResolver; import android.os.Bundle; import android.telephony.TelephonyManager; public class EX05_24 extends ListActivity { private TelephonyManager telMgr; private List item=new ArrayList(); private List value=new ArrayList(); @SuppressWarnings("static-access") @Override public void onCreate(Bundle savedInstanceState) { super.onCreate(savedInstanceState); /* 载入main.xml Layout */ setContentView(R.layout.main); telMgr = (TelephonyManager)getSystemService(TELEPHONY_SERVICE); /* 将取得的信息写入List中 */ /* 取得手机电话号码 */ item.add(getResources().getText(R.string.str_list0).toString()); if(telMgr.getLine1Number()!=null) { value.add(telMgr.getLine1Number()); } else { value.add("无法取得"); } /* 取得电信网络国别 */ item.add(getResources().getText(R.string.str_list1).toString()); if(telMgr.getNetworkCountryIso().equals("")) { value.add("无法取得"); } else { value.add(""+telMgr.getNetworkCountryIso()); } /* 取得电信公司代码 */ item.add(getResources().getText(R.string.str_list2).toString()); if(telMgr.getNetworkOperator().equals("")) { value.add("无法取得"); } else { value.add(telMgr.getNetworkOperator()); } /* 取得电信公司名称 */ item.add(getResources().getText(R.string.str_list3).toString()); if(telMgr.getNetworkOperatorName().equals("")) { value.add("无法取得"); } else { value.add(telMgr.getNetworkOperatorName()); } /* 取得行动通讯类型 */ item.add(getResources().getText(R.string.str_list4).toString()); if(telMgr.getPhoneType()==telMgr.PHONE_TYPE_GSM) { value.add("GSM"); } else { value.add("未知"); } /* 取得网络类型 */ item.add(getResources().getText(R.string.str_list5).toString()); if(telMgr.getNetworkType()==telMgr.NETWORK_TYPE_EDGE) { value.add("EDGE"); } else if(telMgr.getNetworkType()==telMgr.NETWORK_TYPE_GPRS) { value.add("GPRS"); } else if(telMgr.getNetworkType()==telMgr.NETWORK_TYPE_UMTS) { value.add("UMTS"); } else if(telMgr.getNetworkType()==4) { value.add("HSDPA"); } else { value.add("未知"); } /* 取得漫游状态 */ item.add(getResources().getText(R.string.str_list6).toString()); if(telMgr.isNetworkRoaming()) { value.add("漫游中"); } else{ value.add("无漫游"); } /* 取得手机IMEI */ item.add(getResources().getText(R.string.str_list7).toString()); value.add(telMgr.getDeviceId()); /* 取得IMEI SV */ item.add(getResources().getText(R.string.str_list8).toString()); if(telMgr.getDeviceSoftwareVersion()!=null) { value.add(telMgr.getDeviceSoftwareVersion()); } else { value.add("无法取得"); } /* 取得手机IMSI */ item.add(getResources().getText(R.string.str_list9).toString()); if(telMgr.getSubscriberId()!=null) { value.add(telMgr.getSubscriberId()); } else { value.add("无法取得"); } /* 取得ContentResolver */ ContentResolver cv = EX05_24.this.getContentResolver(); String tmpS=""; /* 取得蓝芽状态 */ item.add(getResources().getText(R.string.str_list10).toString()); tmpS=android.provider.Settings.System.getString(cv, android.provider.Settings.System.BLUETOOTH_ON); if(tmpS.equals("1")) { value.add("已开启"); } else{ value.add("未开启"); } /* 取得WIFI状态 */ item.add(getResources().getText(R.string.str_list11).toString()); tmpS=android.provider.Settings.System.getString(cv, android.provider.Settings.System.WIFI_ON); if(tmpS.equals("1")) { value.add("已开启"); } else{ value.add("未开启"); } /* 取得飞行模式是否开启 */ item.add(getResources().getText(R.string.str_list12).toString()); tmpS=android.provider.Settings.System.getString(cv, android.provider.Settings.System.AIRPLANE_MODE_ON); if(tmpS.equals("1")) { value.add("开启中"); } else{ value.add("未开启"); } /* 取得数据漫游是否开启 */ item.add(getResources().getText(R.string.str_list13).toString()); tmpS=android.provider.Settings.System.getString(cv, android.provider.Settings.System.DATA_ROAMING); if(tmpS.equals("1")) { value.add("开启中"); } else{ value.add("未开启"); } /* 使用自定义的MyAdapter来将数据传入ListActivity */ setListAdapter(new MyAdapter(this,item,value)); } }